import { useSelector } from "react-redux"
import { useState } from "react"

const Options = () => {
  const [options, setOptions] = useState([''])

  const handleClick = () => {
    setOptions([...options, ''])
  }

  return (
    <div>
      Add Options:
      {options.map((input, index) => (
        <div>
          <input type='text' />
        </div>)
      )}
      <button onClick={handleClick}>+</button>
    </div>
  )
}

export default Options
