import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  title: '',
  fields: [
    {type: 'dropdown', count: '', data: [] },
    {type: 'textField', count: '', data: [] },
    {type: 'textArea', count: '', data: [] },
    {type: 'switch', count: '', data: [] },
    {type: 'radio', count: '', data: [] }
  ],
  formDataList: []
}

const formSlice = createSlice({
  name: 'form',
  initialState,
  reducers: {
    setTitle: (state, action) => {
      state.title = action.payload;
      console.log('State and action of setTitle reducer: ', state, action)
    },
    addField: (state, action) => {
      state.fields.push(action.payload)
    },
    updateField: (state, action) => {
      const {index, fieldData} = action.payload
      state.fields[index] = { ...state.fields[index], ...fieldData }
    },
    removeField: (state, action) => {
      state.fields.splice(action.payload, 1)
    },
    addForm: (state, action) => {
      state.formDataList.push(action.payload)
    }
  }
})

export const { setTitle, addField, updateField, removeField } = formSlice.actions 
export default formSlice.reducer
