import { useEffect, useState } from 'react';
import './App.css';
import InputForm from './components/InputForm/InputForm';
import { useSelector, useDispatch } from 'react-redux';
import {removeForm} from '../src/components/redux/slices/formSlice'
import DisplayForm from './components/DisplayForm/DisplayForm';

function App() {
  const formData = useSelector(state => state.form)
  const [displayForm, setDisplayForm] = useState(false)
  const [form, setForm] = useState()
  const dispatch = useDispatch()
  console.log('form Data: ', formData)

  const { formDataList } = formData

  const handleClick = (index) => {
    setDisplayForm(true)
    setForm(formDataList[index])
  }

  const handleDelete = (index) => {
    dispatch()
  }

  return (
    <div className="App">
      <InputForm />
      <p>Created Forms: </p>
      {formDataList && 
        formDataList.map((form, index) => (
          <div className='formRow'>
            <span onClick={() => handleClick(index)}>{form.title}</span>
            <button onClick={() => handleClick(index)} className='icons'>üëÅÔ∏è</button>
            <button onClick={() => handleDelete(index)} className='icons'>üóëÔ∏è</button>
          </div>)
        )
      }
      {displayForm && 
        <DisplayForm displayForm={displayForm} setDisplayForm={setDisplayForm} formDetails={form} /> 
      }
    </div>
  );
}

export default App;
