import { useForm } from 'react-hook-form'
import { Box, Button, Modal } from '@mui/material'
import { useReducer, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { setTitle, addField, addForm } from '../redux/slices/formSlice'
import './styles.css'

export const Actions = {
	SetDropdowns: 'setDropdowns',
	SetTextFields: 'setTextFields',
	SetTextAreas: 'setTextAreas',
	SetSwitches: 'setSwitches',
	SetRadios: 'setRadios',
	ZERO: 'zero'
}

function reducer(state, action) {
  switch(action.type) {
    case Actions.SetDropdowns:
      return {...state, dropdownCount: action.payload}

    case Actions.SetTextFields:
      return {...state, textFieldCount: action.payload}

    case Actions.SetTextAreas:
      return {...state, textAreaCount: action.payload}

    case Actions.SetSwitches:
      return {...state, switchCount: action.payload}

    case Actions.SetRadios:
      return {...state, radioCount: action.payload}

    default:
      break
  }
}

const InputForm = () => {
  const [ openForm, setOpenForm ] = useState(false)
  const [ formTitle, setFormTitle ] = useState()

  const [ state, dispatch ] = useReducer(reducer, { 
    dropdownCount: '', 
    textFieldCount: '', 
    textAreaCount: '', 
    switchCount: '', 
    radioCount: '', 
   })

  const globalDispatch = useDispatch()
  const formData = useSelector(state => state.form)
  const { register, handleSubmit } = useForm()

  const onSubmit= (data) => {
    // e.preventDefault()
    if(data && typeof data==='object') {
      console.log('DATA Received: ', data)
      globalDispatch(setTitle(formTitle))
      const fieldData = [
        {type: 'dropdown', count: Number(data.dropdowns), data: []},
        {type: 'textField', count: Number(data.textFields), data: [] },
        {type: 'textArea', count: Number(data.textAreas), data: [] },
        {type: 'switch', count: Number(data.switches), data: [] },
        {type: 'radio', count: Number(data.radios), data: [] }
      ]
      globalDispatch(addField(fieldData))
      const formData = {
        title: formTitle,
        fields: fieldData
      }
      globalDispatch(addForm(formData))
      console.log('fieldData: ', fieldData);
    }



    console.log('DATA Received so far: ',data)
  }

  function updateTextFieldCount(e) {
    dispatch({
      type: Actions.SetTextFields,
      payload: e.target.value
    })
  }

  function updateDropdownCount(e) {
    dispatch({
      type: Actions.SetDropdowns,
      payload: e.target.value
    })
  }

  function updateTextAreaCount(e) {
    dispatch({
      type: Actions.SetTextAreas,
      payload: e.target.value
    })
  }

  function updateSwitchCount(e) {
    dispatch({
      type: Actions.SetSwitches,
      payload: e.target.value
    })
  }

  function updateRadioCount(e) {
    dispatch({
      type: Actions.SetRadios,
      payload: e.target.value
    })
  }

  const handleFormTitle = (title) => {
    console.log('handleFormTitle triggered ', title, ' formData: ', formData);
    setFormTitle(title)
    globalDispatch(setTitle(title))
  }

  console.log('Counts: ', 'dropdowns: ', state.dropdownCount, ', tf: ', state.textFieldCount, ', ta: ', state.textAreaCount, ', swi: ', state.switchCount, ', radio: ', state.radioCount)

  return (
    <div>
      Input Form: 
      <Button onClick={() => setOpenForm(true)}>Create Form</Button>
      <Modal open={openForm}>
        <Box className='inputForm'>
          <div className='inputFields'>
            <form onSubmit={handleSubmit(onSubmit)}>
              <label>Form Title: </label>
              <input 
                type='text' 
                // onChange={e => handleFormTitle(e.target.value)}
                {...register("formTitle")} 
                // value={formTitle}
                placeholder='Enter Your Form Title'
              />

              <p>Number of Input Fields Required for your Form:</p>
              <div className='individualInput'>
                <label>Dropdowns:</label>
                <input 
                  type='number' 
                  // value={state.dropdownCount} onChange={updateDropdownCount} placeholder='0' 
                  className='inputBox'
                  {...register("dropdowns")}
                />
              </div>

              <div className='individualInput'>
                <label>TextFields:</label>
                <input 
                  type='number' 
                  // value={state.textFieldCount} onChange={updateTextFieldCount} placeholder='0' 
                  className='inputBox'
                  {...register("textFields")}
                />
              </div>

              <div className='individualInput'>
                <label>TextAreas:</label>
                <input 
                  type='number' 
                  // value={state.textAreaCount} onChange={updateTextAreaCount} placeholder='0' 
                  className='inputBox'
                  {...register("textAreas")}
                />
              </div>

              <div className='individualInput'>
                <label>Switches:</label>
                <input 
                  type='number' 
                  // value={state.switchCount} onChange={updateSwitchCount} placeholder='0' 
                  className='inputBox'
                  {...register("switches")}
                />
              </div>

              <div className='individualInput'>
                <label>Radio Buttons:</label>
                <input 
                  type='number' 
                  // value={state.radioCount} onChange={updateRadioCount} placeholder='0' 
                  className='inputBox'
                  {...register("radios")}
                />
              </div>

              <input type='submit' className='submitButton' />
            </form>
          </div>
          <button onClick={() => setOpenForm(false)} className='closeButton'>X</button>
        </Box>
      </Modal>
    </div>
  )
}

export default InputForm
